import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getInfo } from '../actions'


function Detail({location: {search}}) {
    let id = search.substring(1);
    const dispatch = useDispatch()
    React.useEffect(() => {
        dispatch(getInfo(id));

    }, [dispatch])


    const moreInfo = useSelector(state => state.moreInfo) 

    // let instructions = [];
    // let final = [];

    // if (!moreInfo) {return <h1>Cargando...</h1>}
    // if (moreInfo.analyzedInstructions.length) {
    //         for (const property in moreInfo.analyzedInstructions[0].steps) {
    //             instructions.push(moreInfo.analyzedInstructions[0].steps[property])
    //         }

    //         final = instructions.map(e => e.step)

    //     }
    if (moreInfo===undefined) {
        return <h1>Cargando...</h1>
    }

        function createSummary() {
            return {__html: moreInfo.summary};
        }

        function createInstructions() {
            return {__html: moreInfo.instructions};
        }

    // console.log(instructions[0].step)

        return (
            <div>

                <h2>{moreInfo.title}</h2>
                <img src={moreInfo.image} alt="img not found" />
                <h3>Diet type</h3>
                <p>{moreInfo.diets.map((diet, index)=> <li key={index}>{diet}</li>)}</p>
                <h3>Instructions</h3>
                <div dangerouslySetInnerHTML={createInstructions()}></div> 
                {/* <p>{final ? final.map((diet, index)=> <li key={index}>{diet}</li>) : null} </p> */}

                <p><b>Score</b>  {moreInfo.spoonacularScore}</p>
                <p><b>Healthyness</b> {moreInfo.healthScore}</p>
                <div dangerouslySetInnerHTML={createSummary()}></div> 
            </div>
        )



}

export default Detail
