import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDiets, getRecipes } from '../../actions'
import './AddRecipe.css'



function AddRecipe() {
    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(getDiets())
    }, [dispatch])
    const dietas = useSelector(state => state.diets)
    const [input, setInput] = useState({
        title: '',
        image: '',
        summary: '',
        spoonacularScore: '',
        healthScore: '',
        instructions: '',
        diets: []
    })

    let handleChange =(e)=> {
        if(dietas.includes(e.target.name)) {
            setInput({
                diets: input.diets.push(e.target.name)
            })
        }
        setInput({
            ...input,
            [e.target.name]:e.target.value
        })


    }
    let handleSubmit = (e) => {
        e.preventDefault()
        axios.post('/recipe', input)
        .then(response => {
            dispatch(getRecipes())
            alert(response)
        })
    }
    return (
        <div className='container'>
            <form className="formAdd">
                <label htmlFor="name">Recipe's name</label>
                <input type="text" placeholder='name of the recipe' name='title' onChange={handleChange}/>
                <label htmlFor="imageUrl">Image url</label>
                <input type="text" placeholder='an image of the recipe' name='image' onChange={handleChange}/>
                <label htmlFor="summary">Summary</label>
                <textarea name="summary" id="qsy" cols="30" rows="10" onChange={handleChange}></textarea>
                <label htmlFor="score">Score</label>
                <input type="number" placeholder='from 0 to 100' name="spoonacularScore" id="1234" onChange={handleChange}/>
                <label>Healthyness</label>
                <input type="number" placeholder='from 0 to 100' name='healthScore' onChange={handleChange}/>
                <label htmlFor="stepbystep">Instructions</label>
                <textarea type="text" placeholder='1...' name='instructions' onChange={handleChange}/>
                <label htmlFor="diets">Diets: </label>            
                <label htmlFor="glutenfree">
                    Gluten free
                <input type="checkbox" name="gluten free" id="4563" onChange={handleChange}/>
                </label>
                <label htmlFor="dairyfree">
                    Dairy Free
                <input type="checkbox" name="dairy free" id="4583" onChange={handleChange}/>
                </label>
                <label htmlFor="vegan">
                    Vegan
                <input type="checkbox" name="vegan" id="4573" onChange={handleChange}/>
                </label>   
                <button onClick={handleSubmit}>CREAR RECETA</button>

            </form>
        </div>  
    )
}

export default AddRecipe
