import React, {useEffect} from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { clearPage, getInfo } from '../../actions'
import Loading from '../Loading/Loading';


function Detail({location: {search}}) {
    let id = search.substring(1);
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(getInfo(id));
        return () => {
            dispatch(clearPage())

        }
    }, [dispatch, id]) 
    let moreInfo = useSelector(state => state.moreInfo) 

    function createSummary() {
        return {__html: moreInfo.summary};
    }

    function createInstructions() {
        return {__html: moreInfo.instructions};
    }
    if(moreInfo === null) {
        return <h1>ERROR 404</h1>
    }

    if (moreInfo?.title!==undefined) {
        return (
            <div>

                    <h2>{moreInfo.title}</h2>
                    <img src={moreInfo.image} alt="img not found" />
                    <h3>Diet type</h3>
                    <p>{moreInfo.createdInDb ? moreInfo.diets.map((diet, index)=> <li key={index}>{diet.name}</li>) : moreInfo.diets.map((diet, index)=> <li key={index}>{diet}</li>)}</p> 
                    {moreInfo.instructions.length ? <p><b>Instructions: </b>{moreInfo.instructions.replace(/(<([^>]+)>)/ig,'')}</p> : null}
                    <h3>Instructions</h3>
                    <div dangerouslySetInnerHTML={createInstructions()}></div> 
                    <p><b>Score</b>  {moreInfo.spoonacularScore}</p>
                    <p><b>Healthyness</b> {moreInfo.healthScore}</p>
                    {/* <div dangerouslySetInnerHTML={createSummary()}></div>  */}
                    <p><b>Summary: </b>{moreInfo.summary.replace(/(<([^>]+)>)/ig,'')}</p>
                </div>
            )
    }   

        return <Loading/>


    }

    export default Detail


