import React from 'react'
import Card from './Card'
import {useSelector} from 'react-redux'

function Cards() {
    const recipe = useSelector(state => state.recipe)//TRAETE DEL ESTADO EL RECIPE

    //Recibo Page, muestro los elementos con indice mayores a inf y menores a sup
    //const Page = 1;
    const Page = useSelector(state => state.Page)
    let inf = 9*Page - 9
    let sup = 9*Page
    if (recipe.length) {
        let show = recipe.filter((_e,i) => i >= inf && i < sup)
        return (<div>
                {show.map((e) => 
                        <Card 
                         key={e.sourceUrl}
                         img={e.image}
                         title={e.title}
                         diets={e.diets}
                         id={e.id}
                         />)
                }
                </div>)
    }

    return <h1>Cargando...</h1>

}

export default Cards

