import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getInfo } from '../actions'

function Detail({location: {search}}) {
    let id = search.substring(1);
    const dispatch = useDispatch()
    dispatch(getInfo(id));
    const moreInfo = useSelector(state => state.moreInfo) 

    function createMarkup() {
        return {__html: moreInfo.summary};
    }
    let instructions = [];
    console.log(moreInfo)
    for (const property in moreInfo.analyzedInstructions[0]) {
        instructions.push(moreInfo.analyzedInstructions[property])
    }


    console.log(instructions)
    return (
        <div>
            <h1>BUENOS DIAS TARDES QUE BARBARO</h1>
            <h2>{moreInfo.title}</h2>
            <img src={moreInfo.image} alt="" />
            {moreInfo.diets.map((diet, index)=> <li key={index}>{diet}</li>)}
            <p>Score: {moreInfo.spoonacularScore}</p>
            <p>Healthyness: {moreInfo.healthScore}</p>
            <div dangerouslySetInnerHTML={createMarkup()}></div> 



        </div>
    )
}

export default Detail
