import React from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {useEffect} from 'react'

function Card() {
  const recipe = useSelector(state => state.recipe)//TRAETE DEL ESTADO EL RECIPE
  useEffect(() => {
    dispatch(getRecipes())

  }, [dispatch])
    return (
        <div>

            <img src={recipe[0].image} alt="img not found" />
            <h1>{recipe[0].title}</h1>
            <p>{recipe[0].diets}</p>


        </div>
    )
}

export default Card
