import {createStore, applyMiddleware} from 'redux'
import thunk from 'redux-thunk'
import { GET_RECIPES, GET_INFO } from '../actions'
const initialState = {
    recipe: [],
    moreinfo: {}

}

const reducer = (state = initialState, action) => {
    switch (action.type) {

    case GET_RECIPES:
        return {
            ...state, 
            recipe: action.payload
        }

        case GET_INFO: 
        return {
            ...state,
            moreinfo: state.recipe.find(e => e.id === action.payload)
        }

    default:
        return state
    }
}
const store = createStore(reducer, applyMiddleware(thunk))

export default store;
