import Card from '../Card/Card'
import {useSelector} from 'react-redux'
import Loading from '../Loading/Loading'


function Cards() {
    const {recipe, search, Score, filtered, Order, Page} = useSelector(state => state)
    // const search = useSelector(state => state.search)
    // const Score = useSelector(state => state.Score)
    // const filtered = useSelector(state => state.filtered)
    // const Order = useSelector(state => state.Order)
    // const Page = useSelector(state => state.Page)
    let show = [];
    let inf = 9*(Page - 1)
    let sup = 9*Page-1
    if (recipe.length) {
       show = [...recipe]
       recipe.forEach((e,i)=> {
           if (e.createdInDb) {
               //console.log(e.diets)
               let aux = e.diets.map(x=> x.name)
               console.log(aux)

            }
       })
    //    if (createdInDb) {
    //     diets = diets.map(e=> e.name)
    // }
       if (filtered.length && filtered!=='none') {
           show = recipe.filter(e=> e.diets.includes(filtered))
        }
        // if (Order===1) {
        //    show.sort((a,b) => (a.title > b.title) ? 1 : -1)}
        // if (Order===-1) {
        //     show.sort((a,b) => (a.title < b.title) ? 1 : -1)}
        //  ...   show.sort((a,b)=> (a.title > b.title)  ? -1 : 1

        if(Order) {
            show.sort((a,b) => (a.title > b.title) ? Order : -Order)
        }
        if(Score) {
            show.sort((a,b) => (a.spoonacularScore > b.spoonacularScore) ? Score : -Score)
        }
        if(search.length) {
            show = show.filter(e=> e.title.toLowerCase().includes(search.toLowerCase()))

        }
        //console.log(recipe[recipe.length-1].diets[1].name)
            show = show.filter((_e,i) => i >= inf && i <= sup)
        return (<div>

            {show.map((e) => 
                <Card 
                key={e.id}
                img={e.image}
                title={e.title}
                diets={e.diets}
                id={e.id}
                     />)
                }
                </div>)
    }

    return <Loading/>

}

export default Cards



