import React, {useState} from 'react'
import Card from './Card'
import {useSelector, useDispatch} from 'react-redux'
import { filterGluten, orderAZ } from '../actions'


function Cards() {
    const recipe = useSelector(state => state.recipe)//TRAETE DEL ESTADO EL RECIPE
    //Recibo Page, muestro los elementos con indice mayores a inf y menores a sup
    //const Page = 1;
    const Order = useSelector(state => state.Order)
    const dispatch = useDispatch()
    // let show = [];
    let [show, setshow] = useState(()=> [])

    const Page = useSelector(state => state.Page)
    const filtered = useSelector(state => state.filtered)
    let inf = 9*(Page - 1)
    let sup = 9*Page-1
    if (recipe.length) {/*
        if (filter activado) => filtro el show por los que cumplan con el filtro
        if (orden cambia) => cambio el inf y el sup puedo usar lenght de show
        */

        if (Order===1) {
            setshow([...recipe])
           let aux = show.sort((a,b) => (a.title > b.title) ? 1 : -1)
           setshow(aux)
           dispatch(orderAZ(-1))
        }
        if (Order===-1) {
            show = recipe.sort((a,b) => (a.title > b.title) ? -1 : 1)}

            if (filtered.length) {
                setshow((prevShow)=> prevShow = recipe.filter(e=>e.diets.includes(filtered)))
                dispatch(filterGluten(''))

            }
            show = recipe.filter((_e,i) => i >= inf && i <= sup)

        return (<div>

            {show.map((e) => 
                <Card 
                key={e.sourceUrl}
                img={e.image}
                title={e.title}
                diets={e.diets}
                id={e.id}
                     />)
                }
                </div>)
    }

    return <h1>Cargando...</h1>

}

export default Cards



