import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { getInfo } from '../actions'
import {useState} from 'react'


function Detail({location: {search}}) {
    let id = search.substring(1);
    const dispatch = useDispatch()
    dispatch(getInfo(id));
    const moreInfo = useSelector(state => state.moreInfo) 
    const [data, setData] = useState({

        final : false
    })
    let instructions;
    // if (!moreInfo) {return <h1>Cargando...</h1>}

    function createMarkup() {
        return {__html: moreInfo.summary};
    }

    if (moreInfo===!undefined) {
        for (const property in moreInfo.analyzedInstructions[0].steps) {
            instructions = instructions.push(moreInfo.analyzedInstructions[0].steps[property])
        }
        //data.final = data.instructions.map(e => e.step)
        instructions = instructions.map(e => e.step)
        setData({
            ...data,
            final: true
        })

    }



    console.log(data.final)
    // console.log(instructions[0].step)
    if (data.final) {
        return (
            <div>

                <h2>{moreInfo.title}</h2>
                <img src={moreInfo.image} alt="" />
                <h3>Diet type</h3>
                <p>{moreInfo.diets.map((diet, index)=> <li key={index}>{diet}</li>)}</p>
                <h3>Instructions</h3>
                {instructions.map((diet, index)=> <li key={index}>{diet}</li>)} 
                â‰ˆ
                <p><b>Score</b>  {moreInfo.spoonacularScore}</p>
                <p><b>Healthyness</b> {moreInfo.healthScore}</p>
                <div dangerouslySetInnerHTML={createMarkup()}></div> 



            </div>
        )

    } 
    return <h1>Cargando...</h1>
}

export default Detail
