import axios from 'axios'
export const GET_RECIPES = 'GET_RECIPES'

export const getRecipes = () => {
    return (dispatch) => {
        axios.get('http://localhost:3001/recipes')
        .then(response => {
            dispatch({type: GET_RECIPES, payload: response.data})
    })
    }
}
