import React from 'react'
import {useSelector} from 'react-redux'

function Card() {

  const recipe = useSelector(state => state.recipe)//TRAETE DEL ESTADO EL RECIPE
    return (
         <div>
            {
                recipe.length>0 ? 
                <div>
            <img src={recipe[0].image} alt="img not found" />
            <h1>{recipe[0].title}</h1>
            <p>{recipe[0].diets}</p>
                </div>
                :
                <h1>Cargando...</h1>
            }

        </div>

    )
}

export default Card
