import axios from 'axios'
export const GET_RECIPES = 'GET_RECIPES'
export const GET_INFO = 'GET_INFO'
export const ADD_PAGE = 'ADD_PAGE'
export const CHANGE_PAGE = 'CHANGE_PAGE'
export const ORDER_AZ = 'ORDER_AZ'
export const ORDER_SCORE = 'ORDER_SCORE'
export const FILTER_DIET = 'FILTER_DIET'
export const SEARCH_RECIPE = 'SEARCH_RECIPE'

export const getRecipes = () => {
    return (dispatch) => {
        axios.get('http://localhost:3001/recipes')
        .then(response => {
            dispatch({type: GET_RECIPES, payload: response.data})
    })
    }
}

export const getInfo = (id) => {
    return (dispatch) => {
        axios.get(`http://localhost:3001/recipes/${id}`)
        .then(response => {
            dispatch({type: GET_INFO, payload: response.data})
        })
        .catch(error => {
            if(error.response?.status) {
                if(error.response.status === 404) {
                    return dispatch({type: GET_INFO, payload: null})

                }
            }
            alert(`ERROR ${error.response.status} ALGO MALIO SAL, SEGURAMENTE CULPA DE LA API`)
        })
    }
}

export const changePage = (i) => {
    return {
    type: CHANGE_PAGE,
    payload: i
    }
}

export const orderAZ = (i) => ({
    type: ORDER_AZ,
    payload: i

})

export const orderScore = (i) => ({
    type: ORDER_SCORE,
    payload: i
})

export const filterDiet = (payload) => ({
    type: FILTER_DIET,
    payload
})

export const searchRecipe = (payload) => ({
    type: SEARCH_RECIPE,
    payload
})





